const { styled } = require("styled-components");

const Form = styled.form`
  display: flex;
  flex-direction: column;
  gap: 15px;
  width: 500px;
  margin: 0 auto;
  padding: 40px 0;
`;

const Label = styled.label`
  display: flex;
  flex-direction: column;
  color: #fff;
  font-family: Roboto, sans-serif;
  font-size: 16px;
  font-weight: 700;
  line-height: normal;
  margin-bottom: 5px;
`;

const Input = styled.input`
  max-width: 500px;
  width: 100%;
  padding: 5px 10px;
`;

const InputFile = styled.input`
  max-width: 500px;
  width: 100%;
`;

export { Form, Label, Input, InputFile };
