import styled from "styled-components";
import ReactPaginate from "react-paginate";

const List = styled.ul`
  display: flex;
  gap: 20px;
  align-items: center;
  justify-content: center;
`;

const Pagination = styled(ReactPaginate)`
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 10px;
  padding: 20px;
`;
export { List, Pagination };
