import styled from "styled-components";

const Item = styled.li`
  width: 400px;
  height: 300px;
  transform: scale(1);
  transition: transform 0.2s ease-in-out;

  &:hover {
    transform: scale(0.5);
  }
`;

const Image = styled.img`
  width: 100%;
  height: 100%;
`;

const NickName = styled.p`
  color: #fff;
  font-family: Roboto, sans-serif;
  font-size: 20px;
  font-weight: 700;
  line-height: normal;
  text-align: center;
  padding: 20px;
`;

export { Item, Image, NickName };
