import { useEffect } from "react";
import { useParams } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { getHeroById } from "../../redux/heroes/operations";
import { selectHeroById, selectIsLoading } from "../../redux/heroes/selectors";

const Details = () => {
  const { heroId } = useParams();
  const dispatch = useDispatch();
  const hero = useSelector((state) => selectHeroById(state, heroId));

  useEffect(() => {
    dispatch(getHeroById(heroId));
  }, [dispatch, heroId]);

  console.log(hero);

  return (
    <>
      {hero && (
        <div>
          <div>
            <img src={hero.images} alt={hero.nickname} />
          </div>
          <div>
            <p>{hero.nickname}</p>
          </div>
        </div>
      )}
    </>
  );
};

export default Details;
