import { createHero, getHeroes } from "../../redux/heroes/operations";
import { useDispatch } from "react-redux";
import { useState } from "react";

const Create = () => {
  const [formData, setFormData] = useState({
    nickname: "",
    realName: "",
    originDescription: "",
    superpowers: "",
    catchPhrase: "",
    images: "",
  });
  const dispatch = useDispatch();

  const handleSaveClick = async () => {
    const formDataToSend = new FormData();
    formDataToSend.append("nickname", formData.nickname);
    formDataToSend.append("realName", formData.realName);
    formDataToSend.append("originDescription", formData.originDescription);
    formDataToSend.append("superpowers", formData.superpowers);
    formDataToSend.append("catchPhrase", formData.catchPhrase);
    formDataToSend.append("images", formData.images);

    await dispatch(createHero(formData));
    // await dispatch(getHeroes());
  };

  const handleChangeInputValue = (e) => {
    const { name, value } = e.target;
    if (name === "images") {
      const file = e.target.files[0];
      setFormData({
        ...formData,
        images: file,
      });
    } else {
      setFormData({
        ...formData,
        [name]: value,
      });
    }
  };
  return (
    <form action="">
      <label htmlFor="">
        <input
          onChange={handleChangeInputValue}
          value={formData.nickname}
          name="nickname"
          type="text"
        />
      </label>
      <label htmlFor="">
        <input
          onChange={handleChangeInputValue}
          value={formData.realName}
          name="realName"
          type="text"
        />
      </label>
      <label htmlFor="">
        <input
          onChange={handleChangeInputValue}
          value={formData.originDescription}
          name="originDescription"
          type="text"
        />
      </label>
      <label htmlFor="">
        <input
          onChange={handleChangeInputValue}
          value={formData.superpowers}
          name="superpowers"
          type="text"
        />
      </label>
      <label htmlFor="">
        <input
          onChange={handleChangeInputValue}
          value={formData.catchPhrase}
          name="catchPhrase"
          type="text"
        />
      </label>
      <label htmlFor="">
        <input type="file" name="images" onChange={handleChangeInputValue} />
      </label>
      <button type="button" onClick={handleSaveClick}>
        save
      </button>
    </form>
  );
};

export default Create;
