import styled from "styled-components";

const FlexContainer = styled.div`
  display: flex;
  align-items: center;
  gap: 40px;
  padding: 40px 0 70px 0;
`;

const ImageBox = styled.div`
  width: 30%;
`;

const Image = styled.img`
  width: 100%;
`;

const ContentBox = styled.div`
  width: 50%;
`;

const Text = styled.p`
  color: #fff;
  font-family: Roboto, sans-serif;
  font-size: 20px;
  font-weight: 700;
  line-height: normal;

  &:not(:last-child) {
    margin-bottom: 10px;
  }
`;

const Button = styled.button`
  width: 150px;
  margin: 0 auto;
  border: none;
  border-radius: 20px;
  padding: 15px 10px;
  color: #274c5b;
  font-family: Roboto, sans-serif;
  font-size: 16px;
  font-weight: 700;
  line-height: normal;
  transition: all 300ms ease;
  transform: scale(1);
  margin-right: 10px;

  &:hover {
    transform: scale(1.05);
  }
`;

const Input = styled.input`
  max-width: 200px;
  width: fit-content;
  padding: 5px 10px;
  margin-top: 10px;
  border-radius: 20px;
  border: none;
  outline: none;
`;

export { FlexContainer, ImageBox, Image, ContentBox, Text, Button, Input };
