import styled from "styled-components";

const FlexContainer = styled.div`
  display: flex;
  align-items: center;
  gap: 40px;
`;

const ImageBox = styled.div`
  width: 30%;
`;

const Image = styled.img`
  width: 100%;
`;

const ContentBox = styled.div`
  width: 50%;
`;

const Text = styled.p`
  color: #274c5b;
  font-family: Roboto, sans-serif;
  font-size: 20px;
  font-weight: 700;
  line-height: normal;

  &:not(:last-child) {
    margin-bottom: 10px;
  }
`;

export { FlexContainer, ImageBox, Image, ContentBox, Text };
