import { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { getHeroById } from "../../redux/heroes/operations";
import { selectHeroById } from "../../redux/heroes/selectors";
import Container from "../Container/Container";
import {
  FlexContainer,
  ImageBox,
  Image,
  ContentBox,
  Text,
} from "./Details.styled";
import Loader from "../Loader/Loader";
import {
  removeHeroById,
  getHeroes,
  updateHero,
} from "../../redux/heroes/operations";

const Details = () => {
  const { heroId } = useParams();
  const [isEdit, setIsEdit] = useState(false);
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const hero = useSelector((state) => selectHeroById(state, heroId));

  const [formData, setFormData] = useState({
    nickname: "",
    real_name: "",
    origin_description: "",
    superpowers: "",
    catch_phrase: "",
    // Add other form fields here and initialize their values as needed.
  });

  useEffect(() => {
    // Set the initial form data from the hero variable when it becomes available.
    if (hero) {
      setFormData({
        nickname: hero.nickname,
        real_name: hero.real_name,
        origin_description: hero.origin_description,
        superpowers: hero.superpowers,
        catch_phrase: hero.catch_phrase,
        // Set other form fields as needed based on hero properties.
      });
    }
  }, [hero]);

  useEffect(() => {
    dispatch(getHeroById(heroId));
  }, [dispatch, heroId]);

  const handleRemoveClick = async () => {
    await dispatch(removeHeroById(heroId));
    await dispatch(getHeroes());
    navigate("/");
  };

  const handleUpdateClick = () => {
    setIsEdit(true);
  };

  const handleSaveClick = () => {
    console.log(formData);
    const updatedData = {
      nickname: formData.nickname,
      real_name: formData.real_name,
      origin_description: formData.origin_description,
      superpowers: formData.superpowers,
      catch_phrase: formData.catch_phrase,
    };

    dispatch(updateHero(heroId, updatedData));
    setIsEdit(false);
  };

  const handleChangeInputValue = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  return (
    <>
      {hero ? (
        <Container>
          <FlexContainer>
            <ImageBox>
              <Image src={hero.images} alt={hero.nickname} />
            </ImageBox>
            <ContentBox>
              <Text>
                nickname:
                {isEdit ? (
                  <input
                    onChange={handleChangeInputValue}
                    value={formData.nickname}
                    name="nickname"
                    type="text"
                  />
                ) : (
                  <span>{hero.nickname}</span>
                )}
              </Text>
              <Text>
                real_name: <span>{hero.real_name}</span>
              </Text>
              <Text>
                origin_description: <span>{hero.origin_description}</span>
              </Text>
              <Text>
                superpowers: <span>{hero.superpowers}</span>
              </Text>
              <Text>
                catch_phrase: <span>{hero.catch_phrase}</span>
              </Text>
              {isEdit ? (
                <button onClick={handleSaveClick}>save</button>
              ) : (
                <button onClick={handleUpdateClick}>edit</button>
              )}
              <button onClick={handleRemoveClick}>remove</button>
            </ContentBox>
          </FlexContainer>
        </Container>
      ) : (
        <Loader />
      )}
    </>
  );
};

export default Details;
