import styled from "styled-components";

const FlexContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 40px;
`;

const ImageBox = styled.div`
  width: 50%;
`;

const Image = styled.div`
  width: 100%;
`;

export { FlexContainer, ImageBox, Image };
