import { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { getHeroById } from "../../redux/heroes/operations";
import { selectHeroById } from "../../redux/heroes/selectors";
import Container from "../Container/Container";
import {
  FlexContainer,
  ImageBox,
  Image,
  ContentBox,
  Text,
} from "./Details.styled";
import Loader from "../Loader/Loader";
import {
  removeHeroById,
  getHeroes,
  updateHero,
} from "../../redux/heroes/operations";

const Details = () => {
  const { heroId } = useParams();
  const [isEdit, setIsEdit] = useState(false);
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const hero = useSelector((state) => selectHeroById(state, heroId));

  useEffect(() => {
    dispatch(getHeroById(heroId));
  }, [dispatch, heroId]);

  const handleRemoveClick = async () => {
    await dispatch(removeHeroById(heroId));
    await dispatch(getHeroes());
    navigate("/");
  };

  const handleUpdateClick = () => {
    dispatch(removeHeroById(heroId));

    navigate("/");
  };

  return (
    <>
      {hero ? (
        <Container>
          <FlexContainer>
            <ImageBox>
              <Image src={hero.images} alt={hero.nickname} />
            </ImageBox>
            <ContentBox>
              <Text>nickname: {hero.nickname}</Text>
              <Text>real_name: {hero.real_name}</Text>
              <Text>origin_description: {hero.origin_description}</Text>
              <Text>superpowers: {hero.superpowers}</Text>
              <Text>catch_phrase: {hero.catch_phrase}</Text>
              <button>edit</button>
              <button onClick={handleRemoveClick}>remove</button>
            </ContentBox>
          </FlexContainer>
        </Container>
      ) : (
        <Loader />
      )}
    </>
  );
};

export default Details;
