import { useEffect } from "react";
import { useParams } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { getHeroById } from "../../redux/heroes/operations";
import { selectHeroById, selectIsLoading } from "../../redux/heroes/selectors";
import Container from "../Container/Container";
import {
  FlexContainer,
  ImageBox,
  Image,
  ContentBox,
  Text,
} from "./Details.styled";

const Details = () => {
  const { heroId } = useParams();
  const dispatch = useDispatch();
  const hero = useSelector((state) => selectHeroById(state, heroId));
  const isLoading = useSelector(selectIsLoading);

  useEffect(() => {
    dispatch(getHeroById(heroId));
  }, [dispatch, heroId]);

  return (
    <>
      {isLoading ? (
        <RotatingLines
          strokeColor="grey"
          strokeWidth="5"
          animationDuration="0.75"
          width="96"
          visible={true}
        />
      ) : (
        <Container>
          <FlexContainer>
            <ImageBox>
              <Image src={hero.images} alt={hero.nickname} />
            </ImageBox>
            <ContentBox>
              <Text>nickname: {hero.nickname}</Text>
              <Text>real_name: {hero.real_name}</Text>
              <Text>origin_description: {hero.origin_description}</Text>
              <Text>superpowers: {hero.superpowers}</Text>
              <Text>catch_phrase: {hero.catch_phrase}</Text>
            </ContentBox>
          </FlexContainer>
        </Container>
      )}
    </>
  );
};

export default Details;
