import { useEffect } from "react";
import { useParams } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { getHeroById } from "../../redux/heroes/operations";
import { selectHeroById, selectIsLoading } from "../../redux/heroes/selectors";
import Container from "../Container/Container";
import { FlexContainer, ImageBox, Image } from "./Details.styled";

const Details = () => {
  const { heroId } = useParams();
  const dispatch = useDispatch();
  const hero = useSelector((state) => selectHeroById(state, heroId));

  useEffect(() => {
    dispatch(getHeroById(heroId));
  }, [dispatch, heroId]);

  return (
    <>
      {hero && (
        <Container>
          <FlexContainer>
            <ImageBox>
              <Image src={hero.images} alt={hero.nickname} />
            </ImageBox>
            <div>
              <p>nickname: {hero.nickname}</p>
              <p>real_name: {hero.real_name}</p>
              <p>origin_description: {hero.origin_description}</p>
              <p>superpowers: {hero.superpowers}</p>
              <p>catch_phrase: {hero.catch_phrase}</p>
            </div>
          </FlexContainer>
        </Container>
      )}
    </>
  );
};

export default Details;
