const { styled } = require("styled-components");

const Form = styled.form`
  display: flex;
  flex-direction: column;
  height: 100vh;
`;

const Label = styled.label`
  display: flex;
  flex-direction: column;
`;

const Input = styled.input`
  max-width: 500px;
  width: 100%;
  padding: 10px 20px;
`;

export { Form, Label, Input };
