import { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import ReactPaginate from "react-paginate";
import { getHeroes } from "../../redux/heroes/operations";
import {
  selectHeroes,
  selectIsLoading,
  selectTotalHeroes,
} from "../../redux/heroes/selectors";
import SuperHeroesItem from "../../components/SuperHeroesItem/SuperHeroesItem";

const SuperHeroesList = () => {
  const [page, setPage] = useState(1);
  const dispatch = useDispatch();
  const heroes = useSelector(selectHeroes);
  const isLoading = useSelector(selectIsLoading);
  const totalHeroes = useSelector(selectTotalHeroes);
  console.log(totalHeroes);
  const perPage = 5;

  useEffect(() => {
    dispatch(getHeroes({ page, limit: perPage }));
  }, [dispatch, page]);

  const pageCount = Math.ceil(totalHeroes / perPage);

  const handlePageClick = ({ selected }) => {
    // selected - это индекс выбранной страницы, начиная с 0
    setPage(selected + 1);
  };

  return (
    <>
      <ul>
        {heroes.map((hero) => (
          <SuperHeroesItem key={hero._id} heroes={hero} />
        ))}
      </ul>
      <ReactPaginate
        previousLabel={"← Previous"}
        nextLabel={"Next →"}
        breakLabel={"..."}
        pageCount={pageCount}
        marginPagesDisplayed={2}
        pageRangeDisplayed={3}
        onPageChange={handlePageClick}
        containerClassName={"pagination"}
        activeClassName={"active"}
      />
    </>
  );
};

export default SuperHeroesList;
