import styled from "styled-components";

const Item = styled.li`
  max-width: 300px;
  height: 400px;
`;

const Image = styled.img`
  width: 100%;
  height: auto;
`;

const NickName = styled.p`
  color: #274c5b;
  font-family: Roboto, sans-serif;
  font-size: 20px;
  font-weight: 700;
  line-height: normal;
`;

export { Item, Image, NickName };
