import { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { getHeroById } from "../../redux/heroes/operations";
import { selectHeroById } from "../../redux/heroes/selectors";
import Container from "../Container/Container";
import {
  FlexContainer,
  ImageBox,
  Image,
  ContentBox,
  Text,
} from "./Details.styled";
import Loader from "../Loader/Loader";
import {
  removeHeroById,
  getHeroes,
  updateHero,
} from "../../redux/heroes/operations";

const Details = () => {
  const { heroId } = useParams();
  const [isEdit, setIsEdit] = useState(false);
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const hero = useSelector((state) => selectHeroById(state, heroId));
  const [formData, setFormData] = useState({
    nickname: "",
    realName: "",
    originDescription: "",
    superpowers: "",
    catchPhrase: "",
    images: "",
  });

  console.log(hero);

  useEffect(() => {
    if (hero) {
      setFormData({
        nickname: hero.nickname,
        realName: hero.realName,
        originDescription: hero.originDescription,
        superpowers: hero.superpowers,
        catchPhrase: hero.catchPhrase,
        images: hero.images,
      });
    }
  }, [hero]);

  useEffect(() => {
    dispatch(getHeroById(heroId));
  }, [dispatch, heroId]);

  const handleRemoveClick = async () => {
    await dispatch(removeHeroById(heroId));
    await dispatch(getHeroes());
    navigate("/");
  };

  const handleUpdateClick = () => {
    setIsEdit(true);
  };

  const handleSaveClick = () => {
    const formDataToSend = new FormData();
    formDataToSend.append("id", heroId);
    formDataToSend.append("nickname", formData.nickname);
    formDataToSend.append("realName", formData.realName);
    formDataToSend.append("originDescription", formData.originDescription);
    formDataToSend.append("superpowers", formData.superpowers);
    formDataToSend.append("catchPhrase", formData.catchPhrase);
    formDataToSend.append("images", formData.images);

    // dispatch(updateHero({ id: heroId, formData }));
    // dispatch(getHeroes());
    setIsEdit(false);
  };

  useEffect(() => {
    if (isEdit) {
      // Вызываем действие для обновления героя
      dispatch(updateHero({ id: heroId, formData }));
      // Сбрасываем флаг редактирования
    }
  }, [dispatch, heroId, formData, isEdit]);

  const handleChangeInputValue = (e) => {
    const { name, value } = e.target;
    if (name === "images") {
      const file = e.target.files[0];
      setFormData({
        ...formData,
        images: file,
      });
    } else {
      setFormData({
        ...formData,
        [name]: value,
      });
    }
  };

  return (
    <>
      {hero ? (
        <Container>
          <FlexContainer>
            {isEdit ? (
              <ImageBox>
                <Image src={hero.images} alt={hero.nickname} />
                <input
                  type="file"
                  name="images"
                  onChange={handleChangeInputValue}
                />
              </ImageBox>
            ) : (
              <ImageBox>
                <Image src={hero.images} alt={hero.nickname} />
              </ImageBox>
            )}
            <ContentBox>
              <Text>
                Nickname:
                {isEdit ? (
                  <input
                    onChange={handleChangeInputValue}
                    value={formData.nickname}
                    name="nickname"
                    type="text"
                  />
                ) : (
                  <span>{hero.nickname}</span>
                )}
              </Text>
              <Text>
                Real name:
                {isEdit ? (
                  <input
                    onChange={handleChangeInputValue}
                    value={formData.realName}
                    name="realName"
                    type="text"
                  />
                ) : (
                  <span>{hero.realName}</span>
                )}
              </Text>
              <Text>
                Origin description:
                {isEdit ? (
                  <input
                    onChange={handleChangeInputValue}
                    type="text"
                    value={formData.originDescription}
                    name="originDescription"
                  />
                ) : (
                  <span>{hero.originDescription}</span>
                )}
              </Text>
              <Text>
                Superpowers:
                {isEdit ? (
                  <input
                    onChange={handleChangeInputValue}
                    type="text"
                    value={formData.superpowers}
                    name="superpowers"
                  />
                ) : (
                  <span>{hero.superpowers}</span>
                )}
              </Text>
              <Text>
                Catch phrase:
                {isEdit ? (
                  <input
                    onChange={handleChangeInputValue}
                    type="text"
                    value={formData.catchPhrase}
                    name="catchPhrase"
                  />
                ) : (
                  <span>{hero.catchPhrase}</span>
                )}
              </Text>
              {isEdit ? (
                <button onClick={handleSaveClick}>save</button>
              ) : (
                <button onClick={handleUpdateClick}>edit</button>
              )}
              <button onClick={handleRemoveClick}>remove</button>
            </ContentBox>
          </FlexContainer>
        </Container>
      ) : (
        <Loader />
      )}
    </>
  );
};

export default Details;
