import { createHero, getHeroes } from "../../redux/heroes/operations";
import { useDispatch } from "react-redux";
import { useEffect, useState } from "react";

const Create = () => {
  const [formData, setFormData] = useState({
    nickname: "",
    realName: "",
    originDescription: "",
    superpowers: "",
    catchPhrase: "",
    images: "",
  });
  const dispatch = useDispatch();

  const handleSaveClick = async () => {
    const formDataToSend = new FormData();
    formDataToSend.append("nickname", formData.nickname);
    formDataToSend.append("realName", formData.realName);
    formDataToSend.append("originDescription", formData.originDescription);
    formDataToSend.append("superpowers", formData.superpowers);
    formDataToSend.append("catchPhrase", formData.catchPhrase);
    formDataToSend.append("images", formData.images);

    await dispatch(createHero({ formData }));
    await dispatch(getHeroes());
  };

  const handleChangeInputValue = (e) => {
    const { name, value } = e.target;
    if (name === "images") {
      const file = e.target.files[0];
      setFormData({
        ...formData,
        images: file,
      });
    } else {
      setFormData({
        ...formData,
        [name]: value,
      });
    }
  };
  return (
    <form action="">
      <label htmlFor="">
        <input type="text" />
      </label>
      <label htmlFor="">
        <input type="text" />
      </label>
      <label htmlFor="">
        <input type="text" />
      </label>
      <label htmlFor="">
        <input type="text" />
      </label>
      <label htmlFor="">
        <input type="text" />
      </label>
      <button onClick={handleSaveClick}>save</button>
    </form>
  );
};

export default Create;
