import { createHero, getHeroes } from "../../redux/heroes/operations";
import { useDispatch } from "react-redux";
import { useState } from "react";

const Create = () => {
  const [formData, setFormData] = useState({
    nickname: "",
    realName: "",
    originDescription: "",
    superpowers: "",
    catchPhrase: "",
    images: null, // Initialize 'images' as null
  });
  const dispatch = useDispatch();

  const handleSaveClick = () => {
    const formDataToSend = new FormData();
    formDataToSend.append("nickname", formData.nickname);
    formDataToSend.append("realName", formData.realName);
    formDataToSend.append("originDescription", formData.originDescription);
    formDataToSend.append("superpowers", formData.superpowers);
    formDataToSend.append("catchPhrase", formData.catchPhrase);
    formDataToSend.append("images", formData.images); // Append 'images' as a file
    console.log(formDataToSend);
    dispatch(createHero(formDataToSend));
    // You can dispatch 'getHeroes()' here if needed
  };

  const handleChangeInputValue = (e) => {
    const { name, value } = e.target;
    if (name === "images") {
      const file = e.target.files[0];
      setFormData({
        ...formData,
        [name]: file, // Set 'images' as a file
      });
    } else {
      setFormData({
        ...formData,
        [name]: value,
      });
    }
  };

  return (
    <form>
      <label>
        Nickname:
        <input
          type="text"
          name="nickname"
          value={formData.nickname}
          onChange={handleChangeInputValue}
        />
      </label>
      <label>
        Real Name:
        <input
          type="text"
          name="realName"
          value={formData.realName}
          onChange={handleChangeInputValue}
        />
      </label>
      <label>
        Origin Description:
        <input
          type="text"
          name="originDescription"
          value={formData.originDescription}
          onChange={handleChangeInputValue}
        />
      </label>
      <label>
        Superpowers:
        <input
          type="text"
          name="superpowers"
          value={formData.superpowers}
          onChange={handleChangeInputValue}
        />
      </label>
      <label>
        Catch Phrase:
        <input
          type="text"
          name="catchPhrase"
          value={formData.catchPhrase}
          onChange={handleChangeInputValue}
        />
      </label>
      <label>
        Images:
        <input type="file" name="images" onChange={handleChangeInputValue} />
      </label>
      <button type="button" onClick={handleSaveClick}>
        Save
      </button>
    </form>
  );
};

export default Create;
